<?php class Base extends CI_Controller{ 	protected $judul = 'e-GueIn'; 	protected $app = 'e-GueIn';  	public function __construct() { 		parent::__construct(); 		$url = $this->uri->segment(1); 		if($url != 'login'){ 			if (!$this->session->userdata('is_logged_in')) { 				redirect('login'); 			} 		}  	} 	public function getKonfig($val){ 		$data_sipp = $this->m_data_sipp->where(array('id'=>62))->get('sys_config')->row_object(); 		$data_sipp2 = $this->m_data_sipp->where(array('id'=>61))->get('sys_config')->row_object(); 		$konfig = array( 			'judul' => $this->judul, 			'instansi' => $data_sipp->value, 			'instansi_pendek' => $data_sipp2->value 		);  		return $konfig[$val]; 	} 	public function get_data(){ 		$data = $this->m_data_sipp->where(array('id'=>82))->get('sys_config')->row_object(); 		$data2 = get_kode($data->value); 		if($data2 == 0){ 			redirect(base_url()); 		}  	} 	public function data(){ 		$data = array( 			'judul' => $this->getKonfig('judul'), 			'instansi_panjang' => $this->getKonfig('instansi'), 			'app'=> $this->app, 			'instansi' => $this->getKonfig('instansi'), 			'instansi_pendek' => $this->getKonfig('instansi_pendek'), 			'user' => $this->session->userdata('username'), 			'status' => $this->session->userdata('idgroup'), 		); 		return $data; 	} } 