<?php defined('BASEPATH') OR exit('No direct script access allowed'); require_once APPPATH . 'controllers/Base.php';  class Buku_tamu extends Base { 	public function lihat($tahun = null) 	{ 		if($tahun == null){ 			$tahunnya = date('Y'); 		}else{ 			$tahunnya = $tahun; 		} 		$data = $this->data(); 		$data['tgl'] = $tahunnya; 		return view('content/laporan/buku_tamu', $data);  	}  	public function datanya($tahun = null){ 		if($tahun == null){ 			$tahunnya = date('Y'); 		}else{ 			$tahunnya = $tahun; 		} 		$draw = intval($this->input->get("draw")); 		$start = intval($this->input->get("start"))+1; 		$length = intval($this->input->get("length")); 		$query = $this->m_data->like(['tanggal'=>$tahunnya])->get('data')->result_object();  		$data = []; 		foreach($query as $r){ 			$data_tamu = $this->m_data->where(['data_id'=>$r->id])->order_by('id','asc')->get('data_tamu'); 			$jumlah_nama_tamu = $data_tamu->num_rows(); 			$nama = ''; 			$ttd = ''; 			$nox = 1; 			if($jumlah_nama_tamu > 0){ 				$data_tamunya = $data_tamu->result_object(); 				foreach ($data_tamunya as $dtm): 					$nama .= "<p class='text_table'>".$nox++.". ".$dtm->nama."</p><br>"; 					if($dtm->ttd != null){ 						$ttd  .= "<img src='".base_url('assets/ttd/'.$dtm->ttd)."' style='width: 100px; height: 50px;'><br>"; 					} 				endforeach; 			}else{ 				$nama .= ''; 				$ttd .= ''; 			} 			$data[] = array( 				"<p class='text_table'>".$start++."</p>", 				"<p class='text_table'>".tanggal_indonesia($r->tanggal)."</p>", 				$nama, 				"<p class='text_table'>".$r->asal."</p>", 				"<p class='text_table'>".$r->surat_tugas."</p>", 				"<p class='text_table'>".$r->keperluan."</p>", 				$ttd,  			); 		} 		$result = array( 			"draw" => $draw, 			"recordsTotal" => count($query), 			"recordsFiltered" => count($query), 			"data" => $data 		); 		echo json_encode($result); 		exit(); 	} }  