<?php function tanggal_indonesia($tanggal = null){ 	if($tanggal == ''){ 		return ''; 	}elseif($tanggal == null){ 		return ''; 	} 	else{ 		$bulan = array ( 			1 =>   'Januari', 			'Februari', 			'Maret', 			'April', 			'Mei', 			'Juni', 			'Juli', 			'Agustus', 			'September', 			'Oktober', 			'November', 			'Desember' 		); 		$pecahkan = explode('-', $tanggal); 		return $pecahkan[2] . ' ' . $bulan[ (int)$pecahkan[1] ] . ' ' . $pecahkan[0]; 	} }  function tanggal_indonesia2($tanggal = null){ 	if($tanggal == ''){ 		return ''; 	}elseif($tanggal == null){ 		return ''; 	} 	else{ 		$bulan = array ( 			1 =>   '01', 			'02', 			'03', 			'04', 			'05', 			'06', 			'07', 			'08', 			'09', 			'10', 			'11', 			'12' 		); 		$pecahkan = explode('-', $tanggal); 		return $pecahkan[2] . '/' . $bulan[ (int)$pecahkan[1] ] . '/' . $pecahkan[0]; 	} } function get_kode($kodenya) { 	$array = array('307122','632064','632043', 	'402540','682260','402531', '307101','402571', 	'307115','402525','402562','401875'); 	if (in_array("".$kodenya."", $array)) { 		$data = '1'; 	}else{ 		$data = '0'; 	} 	return $data; }  function periode_bulan($periode = null){ 	if($periode == ''){ 		return ''; 	}elseif($periode == null){ 		return ''; 	}else{ 		$bulan = array ( 			1 =>   'Januari', 			'Februari', 			'Maret', 			'April', 			'Mei', 			'Juni', 			'Juli', 			'Agustus', 			'September', 			'Oktober', 			'November', 			'Desember' 		); 		$pecahkan = explode('-', $periode); 		return $bulan[ (int)$pecahkan[1] ] . ' ' . $pecahkan[0] ; 	} } 